    VALUES ('20250512115547_StateValueLength', '8.0.15');
    END IF;
END $EF$;
COMMIT;

START TRANSACTION;


DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250512120123_UserFullName') THEN
    ALTER TABLE "Users" ADD "FullName" text;
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250512120123_UserFullName') THEN

                    UPDATE public."Users"
                    SET "FullName" = "FirstName" || ' ' || "LastName"
                
    END IF;
END $EF$;

